          {data.map((ordinate,index) => renderTreeNodes(ordinate,index))}
        {/* </div> */}

        <DetailView
          setDetails={setDetails}
          details={details}
          setOpen={setOpen}
          open={open}
        />
      </div>
    </Box>
  );
};

export default OrgChart;
